import axios from 'axios';
import React, { useEffect, useState } from 'react';
import { useNavigate, useParams } from 'react-router-dom';

const Examupdate = () => {
  const { examId } = useParams();
  const [examName, setExamName] = useState('');
  const [startDate, setStartDate] = useState('');
  const [endDate, setEndDate] = useState('');

  const [subjects, setSubjects] = useState([]);
  const [selectedSubjects, setSelectedSubjects] = useState([]);

  const handleCheckboxChange = (subjectId) => {
    const updatedSelectedSubjects = selectedSubjects.includes(subjectId)
      ? selectedSubjects.filter((id) => id !== subjectId)
      : [...selectedSubjects, subjectId];

    setSelectedSubjects(updatedSelectedSubjects);
  };

  useEffect(() => {
    axios
      .get(`http://localhost:3081/update/${examId}`)
      .then((res) => {
        setExamName(res.data[0].examName);
        setStartDate(res.data[0].startDate);
        setEndDate(res.data[0].endDate);
      })
      .catch((err) => console.log(err));

    // Fetch subjects
    axios
      .get('http://localhost:3081/subjects')
      .then((res) => {
        setSubjects(res.data);

        // Fetch selected subjects for the specific examId
        axios
          .get(`http://localhost:3081/exams/${examId}/subjects`)
          .then((selectedSubjectsRes) => {
            setSelectedSubjects(selectedSubjectsRes.data);
          })
          .catch((err) => console.log(err));
      })
      .catch((err) => console.log(err));
  }, [examId]);

  const formatDate = (dateString) => {
    if (!dateString) {
      return '';
    }
    const date = new Date(dateString);
    const year = date.getFullYear();
    const month = String(date.getMonth() + 1).padStart(2, '0');
    const day = String(date.getDate()).padStart(2, '0');

    return `${year}-${month}-${day}`;
  };

  const navigate = useNavigate();
  const hanldesubmit = (e) => {
    e.preventDefault();
    axios
      .put(`http://localhost:3081/updatedata/${examId}`, {
        examName,
        startDate,
        endDate,
        selectedSubjects,
      })
      .then((res) => {
        if (res.data.updated) {
          navigate('/exams');
        } else {
          alert('Data is not updated');
        }
      });
  };

  return (
    <>
      <div className='update_Exam_from'>
        <h2>Update Exam</h2>
        <form onSubmit={hanldesubmit}>
          <div className='formdiv_contaniner'>
            <label>Exam Name:</label>
            <input type='text' value={examName} onChange={(e) => setExamName(e.target.value)} />
          </div>
          <div className='formdiv_contaniner'>
            <label>Start Date:</label>
            <input
              type='date'
              value={formatDate(startDate)}
              onChange={(e) => setStartDate(e.target.value)}
              min={new Date().toISOString().split('T')[0]}
            />
          </div>
          <div className='formdiv_contaniner'>
            <label>End Date:</label>
            <input
              type='date'
              value={formatDate(endDate)}
              onChange={(e) => setEndDate(e.target.value)}
              min={new Date().toISOString().split('T')[0]}
            />
          </div>
          <div className='formdiv_contaniner'>
            <label>Subjects:</label>
            <ul className='subject-list'>
              {subjects.map((subject) => (
                <li key={subject.subjectId}>
                  <label>
                    <input
                      type='checkbox'
                      checked={selectedSubjects.includes(subject.subjectId)}
                      onChange={() => handleCheckboxChange(subject.subjectId)}
                    />
                    {subject.subjectName}
                  </label>
                </li>
              ))}
            </ul>
          </div>
          <button type='submit'>Update</button>
        </form>
      </div>
    </>
  );
};

export default Examupdate;
