import React, { useState, useEffect } from 'react';
const Dashboard = () => {
    const [courseCount, setCourseCount] = useState(0);
    const [testCount, setTestCount] = useState(0);
    const [questionCount, setQCount] = useState(0);
    useEffect(() => {
        fetch('http://localhost:3081/courses/count')
          .then((response) => {
            if (!response.ok) {
              throw new Error('Network response was not ok');
            }
            return response.json();
          })
          .then((data) => {
            // Assuming data is an array and the count is in the first object
            const firstObject = data[0];
            const totalCount = firstObject ? firstObject.count : 0;
      
            console.log('Data received:', totalCount);
            setCourseCount(totalCount);
          })
          .catch((error) => console.error('Error fetching course count:', error.message));
      }, []);

      useEffect(() => {
        fetch('http://localhost:3081/test/count')
          .then((response) => {
            if (!response.ok) {
              throw new Error('Network response was not ok');
            }
            return response.json();
          })
          .then((data) => {
            // Assuming data is an array and the count is in the first object
            const firstObject = data[0];
            const totalCount = firstObject ? firstObject.count : 0;
      
            console.log('Data received:', totalCount);
            setTestCount(totalCount);
          })
          .catch((error) => console.error('Error fetching course count:', error.message));
      }, []);
    return (
      <div>
          <h2>Total Courses: {courseCount}</h2>
        <h2>Total Tests:{testCount}</h2>
        <h2>Total Questions:{questionCount}</h2>
      </div>
    );
  };
  
  export default Dashboard;
