import React, { useState, useEffect } from 'react';

const Testcreation = ({ testCreationTableId }) => {
  const [testName, setTestName] = useState('');
  const [courses, setCourses] = useState([]);
  const [selectedCourse, setSelectedCourse] = useState('');
  const [startDate, setStartDate] = useState('');
  const [startTime, setStartTime] = useState('');
  const [endDate, setEndDate] = useState('');
  const [endTime, setEndTime] = useState('');
  const [duration, setDuration] = useState('');
  const [totalQuestions, setTotalQuestions] = useState('');
  const [totalMarks, setTotalMarks] = useState('');
  const [calculator, setCalculator] = useState('no');
  const [status, setStatus] = useState('inactive');
  const [typeOfTests, setTypeOfTests] = useState([]);
  const [selectedtypeOfTest, setSelectedtypeOfTest] = useState('');
  const [numberOfSections, setNumberOfSections] = useState(1);
  const [questionLimitChecked, setQuestionLimitChecked] = useState(false);
  const [sectionsData, setSectionsData] = useState([]);
  const [newField1, setNewField1] = useState(''); // Add state for the new fields
  const [newField2, setNewField2] = useState(''); // Add state for the new fields

  useEffect(() => {
    fetch('http://localhost:3081/testcourses')
      .then((response) => response.json())
      .then((data) => setCourses(data))
      .catch((error) => console.error('Error fetching courses:', error));
  }, []);

  useEffect(() => {
    if (selectedCourse) {
      fetch(`http://localhost:3081/course-typeoftests/${selectedCourse}`)
        .then((response) => response.json())
        .then((data) => setTypeOfTests(data))
        .catch((error) => console.error('Error fetching course_typeoftests:', error));
    }
  }, [selectedCourse]);

  useEffect(() => {
    // Fetch data when the component mounts
    const fetchData = async () => {
      try {
        const response = await fetch(`http://localhost:3081/fetch-test-data/${testCreationTableId}`);
        const data = await response.json();
    
        console.log('Fetched data:', data); // Log the fetched data
    
        if (data && Array.isArray(data.sectionsData)) {
          setSectionsData(data.sectionsData);
    
          // Check if testData is available
          if (data.testData) {
            const {
              TestName,
              SelectedCourse,
              selectedtypeOfTest,
              startDate,
              startTime,
              endDate,
              endTime,
              duration,
              totalQuestions,
              totalMarks,
              calculator,
              status,
            } = data.testData;
    
            setTestName(TestName);
            setSelectedCourse(SelectedCourse);
            setSelectedtypeOfTest(selectedtypeOfTest);
            setStartDate(startDate);
            setStartTime(startTime);
            setEndDate(endDate);
            setEndTime(endTime);
            setDuration(duration);
            setTotalQuestions(totalQuestions);
            setTotalMarks(totalMarks);
            setCalculator(calculator);
            setStatus(status);
          } else {
            console.error('No test data available.');
          }
        } else {
          console.error('Invalid data format received from the server:', data);
        }
      } catch (error) {
        console.error('Error fetching data:', error);
      }
    
    
    };

    fetchData();
  }, [testCreationTableId]); // Add other dependencies as needed

  const handleSectionChange = (e, index, field) => {
    // Function to handle changes in section data
    const updatedSectionsData = [...sectionsData];
    updatedSectionsData[index] = {
      ...updatedSectionsData[index],
      [field]: e.target.value,
    };
    setSectionsData(updatedSectionsData);
  };

  const handleSelectChange = (e) => {
    setSelectedCourse(e.target.value);
  };
  const handleSelectTypeOfTest = (e) => {
    setSelectedtypeOfTest(e.target.value);
  };
  const handleInputChange = (e) => {
    setTestName(e.target.value);
  };
  const handleStartDateChange = (e) => {
    setStartDate(e.target.value);
  };

  const handleStartTimeChange = (e) => {
    setStartTime(e.target.value);
  };

  const handleEndDateChange = (e) => {
    setEndDate(e.target.value);
  };

  const handleEndTimeChange = (e) => {
    setEndTime(e.target.value);
  };

  const handleDurationChange = (e) => {
    setDuration(e.target.value);
  };

  const handleTotalQuestionsChange = (e) => {
    setTotalQuestions(e.target.value);
  };

  const handleTotalMarksChange = (e) => {
    setTotalMarks(e.target.value);
  };
  const handleCalculatorChange = (e) => {
    setCalculator(e.target.value);
  };

  const handleStatusChange = (e) => {
    setStatus(e.target.value);
  };

  const handleQuestionLimitChange = (e) => {
    setQuestionLimitChecked(e.target.checked);
  };

  const addSection = () => {
    setNumberOfSections((prevSections) => prevSections + 1);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      console.log('Submitting form with selectedCourse:', selectedCourse);
      console.log('Submitting form with selectedtypeOfTest:', selectedtypeOfTest);

      const response = await fetch('http://localhost:3081/create-test', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          testName,
          selectedCourse,
          selectedtypeOfTest,
          startDate,
          startTime,
          endDate,
          endTime,
          duration,
          totalQuestions,
          totalMarks,
          calculator,
          status,
          sectionsData,
          newField1, // Include the new fields in the request body
          newField2,
        }),
      });

      const data = await response.json();
      console.log(data);
    } catch (error) {
      console.error('Error submitting form:', error);
    }
  };

  return (
    <div>
      <form onSubmit={handleSubmit}>
        <label>
          Test Name:
          <input type="text" value={testName} onChange={handleInputChange} />
        </label>
        <br />
        <label>
          Select Course:
          <select value={selectedCourse} onChange={handleSelectChange}>
            <option value="" disabled>Select a course</option>
            {courses.map((course) => (
              <option key={course.courseCreationId} value={course.courseCreationId}>
                {course.courseName}
              </option>
            ))}
          </select>
        </label>
        <br />

        <div>
          <label>
            Type of Tests:
            <select value={selectedtypeOfTest} onChange={handleSelectTypeOfTest}>
              <option value="" disabled>Select a type of test</option>
              {typeOfTests.map((typeOfTest) => (
                <option key={typeOfTest.TypeOfTestId} value={typeOfTest.TypeOfTestId}>
                  {typeOfTest.TypeOfTestName}
                </option>
              ))}
            </select>
          </label>
        </div>

        {/* New fields */}
        <label>
          New Field 1:
          <input type="text" value={newField1} onChange={(e) => setNewField1(e.target.value)} />
        </label>
        <br />
        <label>
          New Field 2:
          <input type="text" value={newField2} onChange={(e) => setNewField2(e.target.value)} />
        </label>
        <br />

        <label>
          Test  Start Date:
          <input type="date" value={startDate} onChange={handleStartDateChange} />
        </label>
        <label>
          Start Time:
          <input type="time" value={startTime} onChange={handleStartTimeChange} />
        </label>
        <br />
        <label>
          Test End Date:
          <input type="date" value={endDate} onChange={handleEndDateChange} />
        </label>
        <label>
          End Time:
          <input type="time" value={endTime} onChange={handleEndTimeChange} />
        </label>
        <br />
        <label>
          Duration (in minutes):
          <input type="number" value={duration} onChange={handleDurationChange} min="1" />
        </label>
        <br />
        <label>
          Total Questions:
          <input type="number" value={totalQuestions} onChange={handleTotalQuestionsChange} min="1" />
        </label>
        <br />
        <label>
          Total Marks:
          <input type="number" value={totalMarks} onChange={handleTotalMarksChange} min="1" />
        </label>
        <br />
        <div>
          <label>SECTION</label>
          <br />
          <label>
            <input
              type="checkbox"
              checked={questionLimitChecked}
              onChange={handleQuestionLimitChange}
            />Question Limit:
          </label>
          <table>
            <thead>
              <tr>
                <th>#</th>
                <th>Section</th>
                {questionLimitChecked && <th>Question Limit</th>}
                <th>No of Question</th>
              </tr>
            </thead>
            <tbody>
              {Array.from({ length: numberOfSections }, (_, index) => (
                <tr key={index}>
                  <td>{index + 1}</td>
                  <td>
                    <input
                      type="text"
                      value={sectionsData[index]?.sectionName || ''}
                      onChange={(e) => handleSectionChange(e, index, 'sectionName')}
                    />
                  </td>
                  {questionLimitChecked && (
                    <td>
                      <input
                        type="number"
                        value={sectionsData[index]?.QuestionLimit || ''}
                        onChange={(e) => handleSectionChange(e, index, 'QuestionLimit')}
                      />
                    </td>
                  )}
                  <td>
                    <input
                      type="number"
                      value={sectionsData[index]?.noOfQuestions || ''}
                      onChange={(e) => handleSectionChange(e, index, 'noOfQuestions')}
                    />
                  </td>
                </tr>
              ))}
            </tbody>
          </table>

          <button type="button" onClick={addSection}>
            +
          </button>
        </div>
        <br />
        <label>
          Calculator:
          <select value={calculator} onChange={handleCalculatorChange}>
            <option value="yes">Yes</option>
            <option value="no">No</option>
          </select>
        </label>
        <br />
        <label>
          Status:
          <select value={status} onChange={handleStatusChange}>
            <option value="active">Active</option>
            <option value="inactive">Inactive</option>
          </select>
        </label>
        <br />
        <button type="submit">Submit</button>
      </form>

    </div>
  );
};

export default Testcreation;
