import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useParams } from 'react-router-dom';
const NewExamUpdataion = () => {
    const { examId } = useParams();
  const [examName, setExamName] = useState('');
  const [startDate, setStartDate] = useState('');
  const [endDate, setEndDate] = useState('');
  const [subjects, setSubjects] = useState([]);
  const [selectedSubjects, setSelectedSubjects] = useState([]);
   

  useEffect(() => {
    // Fetch subjects from the API
    fetch('http://localhost:3081/subjects')
      .then(response => response.json())
      .then(data => setSubjects(data))
      .catch(error => console.error('Error fetching subjects:', error));
  }, []);

  const handleSubjectChange = subjectId => {
    // Toggle the selected state of the subject
    setSelectedSubjects(prevSelectedSubjects => {
      if (prevSelectedSubjects.includes(subjectId)) {
        return prevSelectedSubjects.filter(subjectId => subjectId !== subjectId);
      } else {
        return [...prevSelectedSubjects, subjectId];
      }
    });
  };

  const fetchExamData = async () => {
    try {
      // Fetch data from the /exams/:examId endpoint
      const response = await axios.get(`http://localhost:3081/feachingexams/${examId}`);
      const examData = response.data[0]; // Assuming only one exam is returned

      setExamName(examData.examName);
      setStartDate(examData.startDate);
      setEndDate(examData.endDate);
      // Assuming examData.subjects is an array of subjectIds
      setSelectedSubjects(examData.subjects || []);
    } catch (error) {
      console.error('Error fetching exam data:', error);
    }
  };

  useEffect(() => {
    // Call the fetchExamData function
    fetchExamData();
  }, [examId]);




  
  const handleSubmit = e => {
    e.preventDefault();
    // You can handle form submission logic here
    console.log('Exam Id:', examId);
    console.log('Exam Name:', examName);
    console.log('Start Date:', startDate);
    console.log('End Date:', endDate);
    console.log('Selected Subjects:', selectedSubjects);
  };

  return (
    <form onSubmit={handleSubmit}>
     <div>
        <label htmlFor="examName">Exam Name:</label>
        <input
          type="text"
          id="examName"
          value={examName}
          onChange={e => setExamName(e.target.value)}
          required
        />
      </div>
      <div>
        <label htmlFor="startDate">Start Date:</label>
        <input
          type="date"
          id="startDate"
          value={startDate}
          onChange={e => setStartDate(e.target.value)}
          required
        />
      </div>
      <div>
        <label htmlFor="endDate">End Date:</label>
        <input
          type="date"
          id="endDate"
          value={endDate}
          onChange={e => setEndDate(e.target.value)}
          required
        />
      </div>
      <div>
        <label>Subjects:</label>
        {subjects.map(subject => (
          <div key={subject.subjectId}>
            <input
              type="checkbox"
              id={subject.subjectId}
              value={subject.subjectId}
              checked={selectedSubjects.includes(subject.subjectId)}
              onChange={() => handleSubjectChange(subject.subjectId)}
            />
            <label htmlFor={subject.subjectId}>{subject.subjectName}</label>
          </div>
        ))}
      </div>
      <button type="submit">Submit</button>
    </form>
  );
};

export default NewExamUpdataion;
